import './App.css';
import { useState } from 'react';
import MyButton from './MyButton';

function App() {

  const [numberOfTimesClicked, setNumberOfTimesClicked] = useState(0)

  const handleBoxClick = () => {
    setNumberOfTimesClicked(numberOfTimesClicked + 1)
  }

  const [lastButtonClicked, setLastButtonClicked] = useState("0")

  return (
    <div className="App">
      <div className="header">
        <h1>Click the BUTTON!</h1>
      </div>

      <br></br>

      <div className="box">
        <p>{lastButtonClicked}</p>
      </div>      

      <br></br>

      <div className="app-button-container">
        <MyButton initial="0" setLastButtonClicked={setLastButtonClicked}/>   

      </div>





    </div>
  );
}

export default App;
