import React from 'react';
import "./MyButton.css";

function MyButton() {

    const [variable, setVariable] = useState(0)

    const handleButtonClick = () => {
        setVariable(variable + 1)
    }

    return (

    <div className="button" onClick={() => handleButtonClick()}>
        <p>Click Me ({variable})</p>
    </div>
  
    )
}

export default MyButton
