import './App.css';
import { useState } from 'react';
import MyButton from './MyButton';

function App() {

  const [numberOfTimesClicked, setNumberOfTimesClicked] = useState(0)
  const handleBoxClick = () => {
    setNumberOfTimesClicked(numberOfTimesClicked + 1)
  }

  return (
    <div className="App">
      <div className="header">
        <h1>Click the BUTTON!</h1>
      </div>
      <br></br>

      <div className="app-button-container">
        <MyButton initial="0"/>   
        <MyButton initial="0"/>  
        <MyButton initial="0"/>   
        <MyButton initial="5"/> 
        <MyButton initial="0"/>   
        <MyButton initial="0"/> 
        <MyButton initial="0"/>   
        <MyButton initial="0"/> 
        <MyButton initial="0"/>   
        <MyButton initial="10"/> 
      </div>


      <div className="box" onClick={() => handleBoxClick()}>
        <p>{numberOfTimesClicked}</p>
      </div>


    </div>
  );
}

export default App;
